import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Login from './pages/Login';
import Home from './pages/Home';

class App extends React.Component {
  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            {this.state.loggenIn ? <Redirect to="/home" /> : <LoginPage onLogin={this.handleLogin} />}
          </Route>
          <Route path="/home">
            {this.state.loggenIn ? <HomePage /> : <Redirect to="/" />}
          </Route>
        </Switch>
      </Router>
    );
  }
}
