import React from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Login from './pages/Login';
import Menu from './pages/Menu';



class App extends React.Component {
  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            {this.state.loggenIn ? <Redirect to="/menu" /> : <Login onLogin={this.handleLogin} />}
          </Route>
          <Route path="/menu">
            {this.state.loggenIn ? <Menu /> : <Redirect to="/" />}
          </Route>
        </Switch>
      </Router>
    );
  }
}

export default App;
