import React from 'react';
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Login from './pages/Login';
import Menu from './pages/Menu';



function App() {
    const [loggedIn, setLoggedIn] = useState(false);
    const history = useHistory();

    const handleLogin = () => {
      setLoggedIn(true);
      history.push('/menu');
    };

    return (
      <Router>
        <Route exact path="/">
          {this.state.loggenIn ? <Redirect to="/menu" /> : <Login onLogin={this.handleLogin} />}
        </Route>
        <Route path="/menu">
          {this.state.loggenIn ? <Menu /> : <Redirect to="/" />}
        </Route>
      </Router>
    );
}

export default App;
